import React from 'react';
import './home.css';
import {useSpring, animated} from 'react-spring';
import { Resume } from './resume';
import linkedin from '../images/linkedin.png';
import email from '../images/email.png';


function Home() {

    let loaded = true;

    const leftColumnTransition = useSpring({ 
        from: {
            opacity: 0,
            marginLeft: 0
            //transform: 'translate3d(0px,0,0)'
        }, 
        to: {
            opacity: 1,
            marginLeft: 20
            //transform: 'translate3d(0px,0,0)'
        },
        config: { duration: 1000},
        reset: false
    });

    return (
        <div>
            <div className="mainContent">


                <animated.div style={leftColumnTransition} className="leftColumn">
                    <h1 className="headingText">Welcome!</h1>
                    <h1 className="nameText">I am Giovanni Sylvestre,</h1>
                    <p className="subText">An aspiring software and mobile developer in Canada.</p>
                    <button className="exploreButton">Explore Portfolio</button>
                </animated.div>
                <div className="rightColumn">
                    <div className="contactInformation">
                        <img src={email} style={{marginRight: 20}} width="80" height="80" />
                        <img src={linkedin}  style={{marginLeft: 20}} width="80" height="80" />
                    </div>
                    <h1 style={{ color: "#ffffff"}}>Skills</h1>
                    <div>
                        <div className="skill">
                            <p>Working</p>
                        </div>
                    </div>
                </div>

            </div>
            <div className="mainContent2">


                <Resume />


            </div>
        </div>
    );
}

export default Home;
