import React from 'react';
import './main.css';
import {useSpring, animated} from 'react-spring';
import linkedin from '../images/linkedin.png';
import GitHubLogo from '../images/github.png';
import Resume from '../resume.pdf';


function Home() {


    const leftColumnTransition = useSpring({ 
        from: {
            opacity: 0,
            marginLeft: 0
        }, 
        to: {
            opacity: 1,
            marginLeft: 20
        },
        config: { duration: 1000}
    });

    const skillsTransition = useSpring({ 
        from: {
            opacity: 0,
            marginTop: 50
        }, 
        to: {
            opacity: 1,
            marginTop: 0
        },
        config: { duration: 2000}
    });

    return (

        <div className="mainContent">

            <animated.div style={leftColumnTransition} className="leftColumn">
                <h1 className="headingText">Welcome!</h1>
                <h1 className="nameText">I am Giovanni Sylvestre,</h1>
                <p className="subText" style={{ fontWeight: 'bold'}}>An aspiring software and mobile developer in Canada.</p>
                <p className="subText">Email: giovannisylvestre@gmail.com</p>
                <button className="exploreButton" onClick={() => window.open(Resume) }>My Resume</button>
            </animated.div>

            <div className="rightColumn">
                <div style={{display: 'flex', flexDirection: 'row'}}>
                    <a href="https://www.linkedin.com/in/giovanni-sylvestre-596303179/" target="_blank" rel="noopener noreferrer"><img src={linkedin} width="80" height="80" /></a>
                    <a href="https://github.com/gsylvest001" target="_blank" rel="noopener noreferrer"><img src={GitHubLogo} style={{marginLeft: 20}} width="80" height="80" /></a>   
                </div>
                <h1 style={{ color: "#ffffff"}}>Skills</h1>

            </div>

        </div>
    );
}

export default Home;
